import React from 'react';

export default class UseLocalStorage {
    setPatients(patients: object): void {
        localStorage.setItem('patients', JSON.stringify(patients));
    }

    setTimeline(timeline: object): void {
        localStorage.setItem('timelineList', JSON.stringify(timeline));
    }

    getPatients(): object {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj;
    }

    getData(): object {
        var data = localStorage.getItem('timelineList');
        var obj = JSON.parse(data!);
        // var dateValue = obj.datetime
        return obj;
    }

    getGender(): string {
        var data = localStorage.getItem('patients');
        var obj = this.getData();
        return obj.gender;
    }

    getAge(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.age;
    }

    getJob(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.job;
    }
}
