}

const styles = StyleSheet.create({
  button: {
    alignItems: "center",
    color: '#012d5e',
    backgroundColor: '#ffc107',
    padding: 5,
    borderRadius: 5,
    fontFamily: 'Prompt',
    margin: 10
  },

  titletext: {
    fontFamily: 'Prompt',
