import React from 'react';
import { StyleSheet, TextInput, TouchableOpacity } from "react-native";
import { DateTimePickerComponent } from '@syncfusion/ej2-react-calendars';
import '../App.css';
import UseLocalStorage from '../useLocalStorage';

function TimelineInput() {
    const dateValue: Date = new Date();
    const [description, onchangeDes] = React.useState('');

    function clickAddData(date: Date, description: string) {
        let storage = new UseLocalStorage();

        // patients from localStorage
        let patients = storage.getPatients();
        let patientsData = { patients };
        console.log(patientsData);

        // timeline from localStorage
        let data = storage.getTimeline();
        let timelineList = [];
        console.log('>>' + data);
        if (data != undefined) {
            timelineList.push(data);
        }

        // add new timeline data
        const newData = {
            datetime: dateValue,
            description: description,
        }
        timelineList.push(newData);
        console.log(timelineList);

        // setLocalStorage
        let covidData = { patients, timelineList };
        storage.setTimeline(newData);
    }

    React.useEffect(() => {
        let storage = new UseLocalStorage();
        let data = storage.getTimeline();
        let timelineList = [];
        timelineList.push(data);
        // console.log(timelineList);

        let patients = storage.getPatients();


        let covidData = { patients, timelineList };
        // console.log(covidData);
        const newData = {
            datetime: '..',
            description: '..',
        }
        timelineList.push(newData);
        covidData = { patients, timelineList };

        console.log(covidData);
        localStorage.setItem('covidData', JSON.stringify(covidData))
    });

    return (
        <div className="inputpadding"
            style={{
                padding: 10, paddingTop: 3, borderRadius: 5, margin: 10
            }}>
            <h1 style={{ color: '#27a742' }}>ข้อมูลไทม์ไลน์</h1>

            <h2>วันเวลา</h2>
            <DateTimePickerComponent
                style={{
                    fontFamily: 'Prompt',
                    backgroundColor: '#fff',
                    padding: 5
                }}
                value={dateValue} />

            <h2>รายละเอียด</h2>
            <TextInput
                value={description}
                onChangeText={onchangeDes}
                multiline
                numberOfLines={4}
                style={styles.textinput}
            />

            <TouchableOpacity
                onPress={() => clickAddData(dateValue, description)}
                style={styles.button}
            >+ เพิ่มข้อมูล
            </TouchableOpacity>
        </div>
    );
}

const styles = StyleSheet.create({
    button: {
        alignItems: "center",
        color: '#012d5e',
        backgroundColor: '#ffc107',
        padding: 5,
        borderRadius: 5,
        fontFamily: 'Prompt',
        margin: 10
    },

    textinput: {
        fontFamily: 'Prompt',
        fontSize: 16,
        height: 40,
        width: '100%',
        padding: 5,
        borderRadius: 5,
        backgroundColor: "#fff"
    }
});

export default TimelineInput;
