import React from 'react';

export default class UseLocalStorage {
    setPatients(patients: object): void {
        localStorage.setItem('patients', JSON.stringify(patients));
    }

    setTimeline(newTimeline: object): void {
        console.log(':: set Timeline');
        var data = JSON.parse(localStorage.getItem('covidData')!);
        console.log(data);
        // if (data.timelineList[0] == null){
        //     console.log(data);
        //     console.log('delete')
        //     delete data.timelineList[0];
        //     console.log(data);
        // }
        localStorage.setItem('covidData', JSON.stringify(newTimeline));
    }

    getPatients(): object {
        var patients = JSON.parse(localStorage.getItem('patients')!);
        return patients;
    }

    getGender(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.gender;
    }

    getAge(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.age;
    }

    getJob(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.job;
    }

    getTimeline(): object {
        var data = localStorage.getItem('covidData');
        console.log(':: get timeline');
        console.log(JSON.parse(data!));
        var timeline = JSON.parse(data!).timelineList;
        console.log(timeline);
        return timeline;
    }
}
