import React from 'react';
import { StyleSheet, Text, View } from "react-native";
import { DateTimePickerComponent } from '@syncfusion/ej2-react-calendars';
import './App.css';
import PatientsInput from './component/patientsinput';
import TimelineInput from './component/timelineinput';
import LocalStorage from './useLocalStorage';


function App() {
  const [selectedLanguage, setSelectedLanguage] = React.useState();
  const dateValue: Date = new Date();

  const items = [{
    title: "May 1940",
    cardTitle: "Dunkirk",
    cardSubtitle: "Men of the British Expeditionary Force (BEF) wade out to..",
    cardDetailedText: "Men of the British Expeditionary Force (BEF) wade out to..",
  }]

  return (
    <div className="App" style={{ padding: 20 }} >
      <Text style={styles.titletext}>COVID Timeline Genetator</Text>
      <div className="App-page" style={{ display: 'flex', flexDirection: 'row' }}>

        <div className="inputData" style={{ flex: '40%' }}>
          <PatientsInput />

          <TimelineInput />
        </div>

        <div
          style={{
            flex: '60%',
            borderColor: '#ffc107',
            borderStyle: 'solid',
            padding: 10,
            margin: 10,
            // width: '100%',
            justifyContent: 'center',
          }}>

          <Text style={[styles.titletext, styles.boldtext,]}>Timeline</Text>

          <View
            style={[styles.button, { borderRadius: 100 }]}
          >
            <h1>ผู้ป่วย {LocalStorage.getTesting()} อายุ  ปี</h1>
            <h2>อาชีพ</h2>
          </View>

          <div className="timeline">
            <div className="container right">
              <div className="content">
                <h2>2017</h2>
                <p>Lorem ipsum..</p>
              </div>
            </div>

            <div className="container right">
              <div className="content">
                <h2>2016</h2>
                <p>Lorem ipsum..</p>
              </div>
            </div>
          </div>

        </div>
      </div>
    </div >
  );
}

const styles = StyleSheet.create({
  button: {
    alignItems: "center",
    color: '#012d5e',
    backgroundColor: '#ffc107',
    padding: 5,
    borderRadius: 5,
    fontFamily: 'Prompt',
    margin: 10
  },

  titletext: {
    fontFamily: 'Prompt',
    fontSize: 28,
    color: '#ffc107',
  },

  boldtext: { fontWeight: 'bold' },

  picker: {
    fontFamily: 'Prompt',
    fontSize: 16,
    height: 40,
    padding: 5,
    borderRadius: 5,
    width: '100%'
  },

  textinput: {
    fontFamily: 'Prompt',
    fontSize: 16,
    height: 40,
    width: '100%',
    padding: 5,
    borderRadius: 5,
    backgroundColor: "#fff"
  }
});

export default App;
