import React from 'react';

export default class UseLocalStorage {
    setPatients(patients: object): void {
        localStorage.setItem('patients', JSON.stringify(patients));
    }

    setTimeline(timeline: object): void {
        // var timelineList = [];
        // timelineList = JSON.parse(localStorage.getItem('timeline')!) || [];
        // var existing = localStorage.getItem('timeline');
        // console.log(existing);
        var timelineList = JSON.parse(localStorage.getItem('timeline')!);
        console.log(timelineList)

        // var checkExist = existing ? existing.split(',') : [];




        // timelineList.push(timeline);
        // localStorage.setItem('timeline', JSON.stringify(timelineList));
    }

    getPatients(): object {
        var patients = JSON.parse(localStorage.getItem('patients')!);
        return patients;
    }

    getGender(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.gender;
    }

    getAge(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.age;
    }

    getJob(): string {
        var data = localStorage.getItem('patients');
        var obj = JSON.parse(data!);
        return obj.job;
    }

    getTimeline(): object {
        var data = localStorage.getItem('covidData');
        var timeline = JSON.parse(data!).timelineList;
        return timeline;
    }
}
